def binary_search(L, v):
	start, end = 0, len(L)-1
	while start != end + 1:
		mid = (start + end) // 2
		if L[mid] < v:
			start = mid + 1
		else:
			end = mid - 1
	if start < len(L) and L[start] == v:
		return start
	else:
		return -1

